    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        limpiarPantalla();
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

        Long dni=Long.parseLong(jtDni.getText());
        String nombre= jtNombre.getText();
        String apellido= jtApellido.getText();
        String ciudad= jtCiudad.getText();
        String domicilio= jtDomicilio.getText();
        
        Cliente clienteNvo= new Cliente(dni,nombre,apellido,ciudad,domicilio);
        
        Long telefono= Long.parseLong(jtTelefono.getText());
       
        boolean exito=MenuView.directorio.agregarCliente(telefono,clienteNvo);
        if(exito) {
            JOptionPane.showMessageDialog(this,"Cliente Agregado exitosamente");
            limpiarPantalla();
        } else {    
            JOptionPane.showMessageDialog(this,"El nro de telfono ya existe, no se agrego");
            limpiarPantalla();
        }
    }//GEN-LAST:event_jButton3ActionPerformed

