    private void jtDniFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtDniFocusLost
        String val="[0-9]*";
        if (!jtDni.getText().matches(val)) {
            JOptionPane.showMessageDialog(this,"Debe ingresar solo numeros en DNI");
            jtDni.requestFocus();
        }
    }//GEN-LAST:event_jtDniFocusLost

    private void jtTelefonoFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtTelefonoFocusLost
       String val="[0-9]*";
        if (!jtTelefono.getText().matches(val)) {
            JOptionPane.showMessageDialog(this,"Debe ingresar solo numeros en TELEFONO");
            jtTelefono.requestFocus();
        }
    }//GEN-LAST:event_jtTelefonoFocusLost

    private void jtNombreFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtNombreFocusLost
        if (jtNombre.getText().length()==0) {
            JOptionPane.showMessageDialog(this,"Complete el campo NOMBRE");
            jtNombre.requestFocus();
        }
    }//GEN-LAST:event_jtNombreFocusLost

    private void jtApellidoFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtApellidoFocusLost
         if (jtApellido.getText().length()==0) {
            JOptionPane.showMessageDialog(this,"Complete el campo APELLIDO");
            jtApellido.requestFocus();
        }
    }//GEN-LAST:event_jtApellidoFocusLost

    private void jtCiudadFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtCiudadFocusLost
        if (jtCiudad.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this,"Complete el campo CIUDAD");
            jtCiudad.requestFocus();
        }
    }//GEN-LAST:event_jtCiudadFocusLost

    private void jtDomicilioFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jtDomicilioFocusLost
        if (jtDomicilio.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Complete el campo DOMICILIO");
            jtDomicilio.requestFocus();
        }
    }//GEN-LAST:event_jtDomicilioFocusLost

    private void limpiarPantalla() {
        jtDni.setText("");
        jtApellido.setText("");
        jtNombre.setText("");
        jtCiudad.setText("");
        jtDomicilio.setText("");
        jtTelefono.setText("");
    }
    
